import { num, string, negative, getThemeValue } from '@xstyled/util'
import { CSSScalar, TransformValue } from './types'

interface PxToRemOptions {
  rootFontSize?: number
}

const round = (value: number): number => Math.round(value * 10 ** 4) / 10 ** 4

export const unit =
  (unit: string) =>
  <T extends CSSScalar>(value: T): string | T =>
    num(value) && value !== 0 ? `${value}${unit}` : value

export const ms = unit('ms')
export const px = unit('px')
export const deg = unit('deg')

const pxToRem = (
  value: number,
  { rootFontSize = 16 }: PxToRemOptions = {},
): number => round(value / rootFontSize)

export const remPx = (
  value: CSSScalar,
  options?: PxToRemOptions,
): CSSScalar => {
  const num = Number(value)
  if (Number.isNaN(num) || num === 0) return value
  return `${pxToRem(num, options)}rem`
}

export const rpx = (value: CSSScalar, options?: PxToRemOptions): CSSScalar => {
  if (!string(value) || value.length < 4) return value
  const unit = value.slice(-3)
  if (unit !== 'rpx') return value
  const n = Number(value.slice(0, value.length - 3))
  if (n === 0) return 0
  return `${pxToRem(n, options)}rem`
}

export const percent = (n: CSSScalar): CSSScalar =>
  num(n) && n !== 0 && n >= -1 && n <= 1 ? `${round(n * 100)}%` : n

export const transformNegative: TransformValue = (
  _,
  { rawValue, variants, props },
) => {
  if (string(rawValue)) {
    const neg = rawValue.startsWith('-')
    const abs = neg ? rawValue.substr(1) : rawValue
    const varVal = getThemeValue(props, abs, variants)
    const value = string(varVal) || num(varVal) ? varVal : abs
    return neg ? `-${value}` : value
  }
  if (num(rawValue)) {
    const neg = negative(rawValue)
    const abs = Math.abs(rawValue)
    const varVal = variants ? variants[abs] : undefined
    if (string(varVal)) return neg ? `-${varVal}` : varVal
    const value = num(varVal) ? varVal : abs
    return neg ? value * -1 : value
  }
  return undefined
}
import { px } from './unit'
import { Screens } from './types'

export const mediaMinWidth = (value: string | null): string | null =>
  value ? `@media (min-width: ${value})` : null
export const mediaMaxWidth = (value: string | null): string | null =>
  value ? `@media (max-width: ${value})` : null
export const mediaBetweenWidth = (
  min: string | null,
  max: string | null,
): string | null =>
  min && max ? `@media (min-width: ${min}) and (max-width: ${max})` : null

/**
 * Minimum breakpoint width.
 * Null for the smallest breakpoint.
 */
export const getBreakpointMin = <T extends Screens>(
  screens: T,
  key: keyof T,
): string | null => {
  const value = screens[key]
  return value === 0 ? null : (px(value) as string)
}

/**
 * Maximum breakpoint width. Null for the largest (last) breakpoint.
 * The maximum value is calculated as the minimum of the next one less 0.02px
 * to work around the limitations of `min-` and `max-` prefixes and viewports with fractional widths.
 * See https://www.w3.org/TR/mediaqueries-4/#mq-min-max
 * Uses 0.02px rather than 0.01px to work around a current rounding bug in Safari.
 * See https://bugs.webkit.org/show_bug.cgi?id=178261
 */
export const getBreakpointMax = <T extends Screens>(
  screens: T,
  key: keyof T,
): string | null => {
  const value = screens[key]
  return value === 0 ? null : (px(value - 0.02) as string)
}
import { Screens, States, Props } from './types'
import { mediaMinWidth, getBreakpointMin } from './media'
import { XCache } from './cache'

type PropsScreens<T extends Props> = T['theme'] extends { screens: Screens }
  ? T['theme']['screens']
  : Screens

export const getScreens = <T extends Props>(props: T): PropsScreens<T> => {
  return (
    props.theme && props.theme.screens ? props.theme.screens : {}
  ) as PropsScreens<T>
}

type PropsStates<T extends Props> = T['theme'] extends { states: States }
  ? T['theme']['states']
  : Screens

export const getStates = <T extends Props>(props: T): PropsStates<T> => {
  return (
    props.theme && props.theme.states ? props.theme.states : {}
  ) as PropsStates<T>
}

type PropsScreensVariants<T extends Props> = {
  [P in keyof PropsScreens<T>]: string | null
}

export type PropsVariants<T extends Props> = PropsScreensVariants<T> &
  PropsStates<T>

export const getVariants = <T extends Props>(props: T): PropsVariants<T> => {
  const screens = getScreens(props)
  const states = getStates(props)
  const medias = {} as PropsScreensVariants<T>
  for (const value in screens) {
    medias[value] = mediaMinWidth(getBreakpointMin(screens, value))
  }
  const variants = { ...medias, ...states }

  // Move at-rules to the end, since they don't increase specificity by
  // themselves but might need to override something that does.
  // See https://github.com/gregberge/xstyled/issues/288
  for (const [value, selector] of Object.entries(variants)) {
    if (selector && selector.startsWith('@')) {
      delete variants[value]
      // @ts-ignore
      variants[value] = selector
    }
  }

  return variants
}

export const getCachedVariants = <T extends Props>(
  props: T,
  cache: XCache<PropsVariants<T>>,
): PropsVariants<T> => {
  if (cache.has('_variants')) return cache.get('_variants') as PropsVariants<T>
  const states = getVariants(props)
  cache.set('_variants', states)
  return states
}
import { ITheme } from './types'

export interface XCache<T> {
  has(key: unknown): boolean
  set(key: unknown, value: T): void
  get(key: unknown): T | undefined
}

interface ThemeCache {
  [key: string]: XCache<any>
}

const cacheSupported: boolean =
  typeof Map !== 'undefined' && typeof WeakMap !== 'undefined'

const caches = cacheSupported ? new WeakMap<ITheme, ThemeCache>() : null

const isCacheDisabled = (theme: ITheme): boolean =>
  theme?.xstyled?.cache === false

const getThemeCache = (theme: ITheme): ThemeCache | null => {
  const cacheDisabled = isCacheDisabled(theme)
  if (cacheDisabled) return null
  if (caches === null) return null
  if (caches.has(theme)) return caches.get(theme) || null
  const cache = {}
  caches.set(theme, cache)
  return cache
}

const noopCache: XCache<any> = {
  has: () => false,
  set: () => undefined,
  get: () => undefined,
}

export const getCache = <T>(
  theme: ITheme | undefined,
  namespace: string,
): XCache<T> => {
  if (!theme) return noopCache
  const cache = getThemeCache(theme)
  if (!cache || !theme) return noopCache
  cache[namespace] = cache[namespace] || new Map()
  return cache[namespace]
}
/* eslint-disable no-continue, no-underscore-dangle, no-restricted-syntax, guard-for-in, no-multi-assign */
import {
  is,
  num,
  func,
  string,
  obj,
  getThemeValue,
  warn,
  merge,
  assign,
  cascade,
} from '@xstyled/util'
import { getCachedVariants, PropsVariants } from './theme'
import { getCache } from './cache'
import {
  CSSScalar,
  CSSObject,
  Props,
  Transformers,
  ThemeNamespace,
  CSSFromProps,
  Theme,
  ThemeGetter,
  TransformValue,
  StyleGenerator,
  Mixin,
  StyleOptions,
  CSSOption,
  StyleGeneratorPropsConcat,
} from './types'

let themeGetterId = 0

type Splitter = [RegExp, string]

const SPLITTERS: { [key: string]: Splitter } = {
  shorthand: [/\s+/, ' '],
  multiple: [/\s*,\s*/, ','],
}

const splitValue =
  (splitter: Splitter, transform: (v: string) => any) => (value: string) =>
    value.split(splitter[0]).map(transform).join(splitter[1])

export const themeGetter = <T = any>({
  name,
  transform: defaultTransform,
  key,
  compose,
  shorthand,
  multiple,
}: {
  name?: string
  key?: string
  transform?: TransformValue
  compose?: ThemeGetter
  shorthand?: boolean
  multiple?: boolean
}): ThemeGetter<T> => {
  const id = themeGetterId++
  const getter =
    (value: unknown, defaultValue?: CSSScalar) => (props: Props<Theme>) => {
      let res = value
      if (!string(value) && !num(value) && value !== true) {
        return res as CSSScalar
      }
      const cacheKey = `${typeof value}-${value}-${defaultValue}`
      const cache = getCache<CSSScalar>(props.theme, `__themeGetter${id}`)
      if (cache.has(cacheKey)) return cache.get(cacheKey)

      const getValue = (value: string | number | true) => {
        const localDefaultValue = is(defaultValue) ? defaultValue : value
        let res: string | number | true | undefined | null = value
        const variants = is(key)
          ? (getThemeValue(props, key) as ThemeNamespace)
          : null
        if (is(variants)) {
          const path =
            value === true
              ? 'default'
              : string(value) || num(value)
              ? value
              : null
          if (is(path)) {
            const fromTheme = getThemeValue(props, path, variants)
            res = Array.isArray(fromTheme)
              ? fromTheme.join(',')
              : (fromTheme as string | number | true)
          }
        }
        let rawValue: unknown = value
        if (!is(res)) {
          rawValue = localDefaultValue
          res = localDefaultValue
        }
        const transform =
          (name && props.theme && props.theme.transformers
            ? (props.theme.transformers as Transformers)[name]
            : null) || defaultTransform
        if (transform) {
          res = transform(res, {
            rawValue,
            variants,
            props,
          })
        }
        return compose ? compose(res)(props) : res
      }

      if ((shorthand || multiple) && string(value)) {
        let transform: (value: string) => any = getValue
        if (shorthand) transform = splitValue(SPLITTERS.shorthand, transform)
        if (multiple) transform = splitValue(SPLITTERS.multiple, transform)
        res = transform(value)
      } else {
        res = getValue(value)
      }

      cache.set(cacheKey, res as CSSScalar)
      return res as CSSScalar
    }
  getter.meta = { name, transform: defaultTransform }
  return getter
}

export const createStyleGenerator = <TProps extends Props = {}>({
  getStyle,
  props,
  cssGetters = {},
  generators,
}: {
  getStyle: CSSFromProps<Props<Theme> & TProps>
  props: string[]
  cssGetters?: { [key: string]: ThemeGetter }
  generators?: StyleGenerator[]
}): StyleGenerator<TProps> => {
  const generator = getStyle as unknown as StyleGenerator
  generator.meta = {
    props,
    cssGetters,
    getStyle: generator,
    generators,
  }
  generator.apply =
    (values: { [key: string]: unknown }) =>
    ({ theme }: Props<Theme>) =>
      generator({ theme, ...values })
  return generator
}

export const reduceVariants = <T extends Props>(
  props: T,
  values: { [key: string]: unknown; [key: number]: unknown },
  getStyle: (value: any) => CSSObject | null | undefined,
): CSSObject => {
  const cache = getCache<PropsVariants<T>>(props.theme, '__variants')
  const variants = getCachedVariants(props, cache)
  let styles = {} as CSSObject
  for (const value in values) {
    const style = getStyle(values[value])
    if (style === null) continue
    const state = value in variants ? variants[value] : value
    if (state === undefined) continue
    if (state === null) {
      styles = merge(styles, style)
    } else {
      styles[state] = styles[state] ? assign(styles[state], style) : style
    }
  }
  return styles
}

const getStyleFactory = (
  prop: string,
  mixin: Mixin,
  themeGet?: ThemeGetter,
): CSSFromProps => {
  return (props: Props<Theme>) => {
    const fromValue = (value: unknown): CSSObject | null | undefined => {
      if (!is(value)) return null
      if (obj(value)) return reduceVariants(props, value, fromValue)
      return cascade(mixin(themeGet ? themeGet(value)(props) : value), props)
    }

    const value = props[prop]
    if (!is(value)) return null
    const cache = getCache<CSSObject | null | undefined>(props.theme, prop)
    const key = obj(value) ? JSON.stringify(value) : value
    if (cache.has(key)) return cache.get(key)
    const style = fromValue(value)
    cache.set(key, style)
    return style
  }
}

const indexGeneratorsByProp = (
  generators: StyleGenerator[],
): {
  [key: string]: StyleGenerator
} => {
  const index: { [key: string]: StyleGenerator } = {}
  for (let i = 0; i < generators.length; i++) {
    const style = generators[i]
    if (style && style.meta) {
      for (let j = 0; j < style.meta.props.length; j++) {
        const prop = style.meta.props[j]
        index[prop] = style
      }
    }
  }
  return index
}

const sortStyles = (
  styles: CSSObject,
  variants: { [key: string]: string },
): CSSObject => {
  for (const key in variants) {
    const variant = variants[key]
    const style = styles[variant]
    if (!style) continue
    delete styles[variant]
    styles[variant] = style
  }
  return styles
}

export function compose<TProps = {}>(
  ...generators: StyleGenerator[]
): StyleGenerator<TProps>
export function compose<T extends StyleGenerator[]>(
  ...generators: T
): StyleGenerator<StyleGeneratorPropsConcat<T>>

export function compose(...generators: any[]): any {
  let flatGenerators: StyleGenerator[] = []

  generators.forEach((gen) => {
    warn(Boolean(gen), `Undefined generator in "compose" method`)
    if (!gen) return
    if (gen.meta.generators) {
      flatGenerators = [...flatGenerators, ...gen.meta.generators]
    } else {
      flatGenerators.push(gen)
    }
  })

  const generatorsByProp = indexGeneratorsByProp(flatGenerators)

  const getStyle = (props: Props<Theme>, sort = true) => {
    const styles = {} as CSSObject

    let merged
    for (const key in props) {
      const generator = generatorsByProp[key]
      if (generator) {
        const style = generator.meta.getStyle(props, false)
        merge(styles, style)
        merged = true
      }
    }

    if (!merged || !sort) return styles

    const medias = getCachedVariants(props, getCache(props.theme, '__states'))
    return sortStyles(styles, medias)
  }

  const props = [] as string[]
  const cssGetters = {} as { [key: string]: ThemeGetter }
  for (let i = 0; i < flatGenerators.length; i++) {
    const generator = flatGenerators[i]
    props.push(...generator.meta.props)
    Object.assign(cssGetters, generator.meta.cssGetters)
  }

  return createStyleGenerator({ getStyle, props, cssGetters, generators })
}

const getMixinFromCSSProperties =
  (properties?: string | string[]): Mixin =>
  (value) => {
    if (string(properties)) return { [properties]: value } as CSSObject
    const style = {} as CSSObject
    for (const key in properties) {
      style[properties[key as unknown as number]] = value as CSSObject
    }
    return style
  }

const getMixinFromCSSOption = (css: CSSOption): Mixin => {
  if (func(css)) return css
  return getMixinFromCSSProperties(css)
}

const dasherize = (key: string) => key.replace(/[A-Z]/g, '-$&').toLowerCase()

export const style = <TProps extends Props = {}>({
  prop,
  css,
  themeGet,
  key,
  transform,
  cssProps: cssPropsOption,
}: StyleOptions): StyleGenerator<TProps> => {
  const getter =
    themeGet || (key || transform ? themeGetter({ key, transform }) : undefined)
  const cssProps =
    cssPropsOption ||
    (string(css)
      ? [css]
      : Array.isArray(css)
      ? css
      : string(prop)
      ? [prop]
      : Array.isArray(prop)
      ? prop
      : [])

  if (Array.isArray(prop)) {
    const mixin = css ? getMixinFromCSSOption(css) : css
    const generators = prop.map((prop) =>
      style({ prop, css: mixin, cssProps, themeGet: getter }),
    )
    return compose(...generators)
  }

  const props = [prop] as string[]
  const mixin = getMixinFromCSSOption(css || props)

  const generators = [] as StyleGenerator[]
  const getStyle = getStyleFactory(prop as string, mixin, getter)
  const cssGetters = getter
    ? cssProps.reduce((getters, cssProp) => {
        getters[dasherize(cssProp)] = getter
        return getters
      }, {} as { [key: string]: ThemeGetter })
    : {}
  const generator = createStyleGenerator({ getStyle, props, cssGetters })
  generators.push(generator)
  return compose(...generators)
}
import { themeGetter } from '../style'
import { px, ms, deg, rpx, percent } from '../unit'
import { ITheme, Theme, ThemeNamespaceValue } from '../types'

export type Pixel = number | string
export const getPx = themeGetter<Pixel>({
  name: 'px',
  transform: (value, { props }) => {
    const rootFontSize = props?.theme?.settings?.rootFontSize ?? undefined
    const num = Number(value)
    return px(rpx(Number.isNaN(num) ? value : num, { rootFontSize }))
  },
})

export type ThemeDuration<T extends ITheme = Theme> = ThemeNamespaceValue<
  'durations',
  T
>
export type Duration<T extends ITheme = Theme> =
  | ThemeDuration<T>
  | number
  | string
export const getDuration = themeGetter<Duration>({
  name: 'duration',
  key: 'durations',
  transform: (value) => {
    const num = Number(value)
    return ms(Number.isNaN(num) ? value : num)
  },
})

export type Angle = number | string
export const getAngle = themeGetter<Angle>({
  name: 'angle',
  transform: (value) => {
    const num = Number(value)
    return deg(Number.isNaN(num) ? value : num)
  },
})

export type Percent = number | string
export const getPercent = themeGetter<Percent>({
  name: 'percent',
  compose: getPx,
  transform: percent,
})
import * as CSS from 'csstype'
import { style, themeGetter, compose } from '../style'
import { getDuration, Duration } from './units'
import { SystemProp, ITheme, Theme, ThemeNamespaceValue } from '../types'

export type ThemeTransition<T extends ITheme = Theme> = ThemeNamespaceValue<
  'transitions',
  T
>
export const getTransition = themeGetter<ThemeTransition>({
  name: 'transition',
  key: 'transitions',
})

export type ThemeTransitionProperty<T extends ITheme = Theme> =
  ThemeNamespaceValue<'transitionProperties', T>
export const getTransitionProperty = themeGetter<ThemeTransitionProperty>({
  name: 'transitionProperty',
  key: 'transitionProperties',
})

export type ThemeTimingFunction<T extends ITheme = Theme> = ThemeNamespaceValue<
  'timingFunctions',
  T
>
export const getTimingFunction = themeGetter<ThemeTimingFunction>({
  name: 'timingFunction',
  key: 'timingFunctions',
})

export interface TransitionProps<T extends ITheme = Theme> {
  transition?: SystemProp<ThemeTransition<T> | CSS.Property.Transition, T>
}
export const transition = style<TransitionProps>({
  prop: 'transition',
